    const unsigned char *TestImage::get_image_buffer() const {
        return M_S_TEST_IMAGE.data();
    }

    unsigned TestImage::get_number_of_components() const {
        return 4;
    }

    std::string TestImage::get_component_name(unsigned component) const {
        switch (component) {
            case 0:
                return "red";
            case 1:
                return "green";
            case 2:
                return "blue";
            case 3:
                return "alpha";
            default:
                assert(false);
        }
    }

    long TestImage::get_image_buffer_size() const {
        return static_cast<long>(M_S_TEST_IMAGE.size());
    }

    unsigned TestImage::get_image_width() const {
        return M_S_SIZE[0];
    }

    unsigned TestImage::get_image_height() const {
        return M_S_SIZE[1];
    }

    unsigned TestImage::get_image_bytes_per_pixel() const {
        auto bytes_per_component = ((get_bit_depth() - 1) / 8) + 1;
        return bytes_per_component * get_number_of_components();
    }

    unsigned TestImage::get_bit_depth() const {
        return M_S_BIT_DEPTH;
    }

